def apply_bulk_discount(subtotal: float) -> float:
    if subtotal > BULK_DISCOUNT_THRESHOLD_HIGH:
        return subtotal * BULK_DISCOUNT_RATE_HIGH
    elif subtotal > BULK_DISCOUNT_THRESHOLD_LOW:
        return subtotal * BULK_DISCOUNT_RATE_LOW
    return subtotal

def apply_membership_discount(total: float, membership: str) -> float:
    if membership == 'vip':
        return total * VIP_DISCOUNT_RATE
    return total

def calculate_order_total(order: Order) -> float:
    subtotal = calculate_subtotal(order.items)
    after_bulk = apply_bulk_discount(subtotal)
    final_total = apply_membership_discount(after_bulk, order.membership)
    return final_total
