body {
    font-family: 'Malgun Gothic', sans-serif;
}

.dashboard {
    display: flex;
    flex-wrap: wrap;
    min-height: 100vh;
}

/* 헤더: 전체 너비 */
.header {
    flex: 1 1 100%;
    background: #2c3e50;
    color: white;
    padding: 1rem 2rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.header nav a {
    color: white;
    text-decoration: none;
    margin-left: 1.5rem;
}

.header nav a:hover {
    text-decoration: underline;
}

/* 사이드바 */
.sidebar {
    flex: 0 0 200px;
    background: #34495e;
    color: white;
    padding: 1rem;
}

.sidebar ul {
    list-style: none;
}

.sidebar li {
    padding: 0.75rem 1rem;
    border-radius: 5px;
    transition: background 0.3s;
}

.sidebar li:hover {
    background: #3d566e;
}

.sidebar a {
    color: white;
    text-decoration: none;
}

/* 메인 컨텐츠 */
.main-content {
    flex: 1 1 calc(100% - 200px);
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    padding: 20px;
    background: #ecf0f1;
    align-content: flex-start;
}

.card {
    flex: 1 1 calc(50% - 20px);
    min-width: 250px;
    background: white;
    padding: 30px;
    border-radius: 10px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
    text-align: center;
    font-size: 1.2rem;
    color: #333;
}

/* 푸터 */
.footer {
    flex: 1 1 100%;
    background: #2c3e50;
    color: white;
    padding: 1rem;
    text-align: center;
}

/* 반응형 */
@media (max-width: 768px) {
    .sidebar {
        flex: 1 1 100%;
        order: 2;
    }

    .main-content {
        flex: 1 1 100%;
        order: 3;
    }

    .footer {
        order: 4;
    }

    .card {
        flex: 1 1 100%;
    }
}

