  const html = matches.map(keyword => {
    // ê²€ìƒ‰ì–´ ê°•ì¡° í‘œì‹œ
    const highlighted = keyword.replace(
      new RegExp(query, 'gi'),
      match => `<strong>${match}</strong>`
