    });
  });
}

// âœ… í‚¤ì›Œë“œ ì„ íƒ í•¨ìˆ˜
function selectKeyword(keyword) {
  searchInput.value = keyword;
  suggestionsDiv.style.display = 'none';

  // ê²€ìƒ‰ ê¸°ë¡ì— ì¶”ê°€
  addToHistory(keyword);

  // ì‹¤ì œ ê²€ìƒ‰ (ì—¬ê¸°ì„œëŠ” ì½˜ì†” ì¶œë ¥)
  console.log('ê²€ìƒ‰:', keyword);
