// ğŸ“‹ ê²€ìƒ‰ ê¸°ë¡ í‘œì‹œ
function displayHistory() {
  if (searchHistory.length === 0) {
    historyDiv.innerHTML = '<p style="color: #999;">ê²€ìƒ‰ ê¸°ë¡ì´ ì—†ìŠµë‹ˆë‹¤.</p>';
    return;
  }

  const html = searchHistory.map((keyword, index) => `
    <div class="history-item">
      <span>${keyword}</span>
      <span class="delete-btn" data-index="${index}">ì‚­ì œ</span>
    </div>
  `).join('');

  historyDiv.innerHTML = html;

  // ì‚­ì œ ë²„íŠ¼ ì´ë²¤íŠ¸
  const deleteBtns = historyDiv.querySelectorAll('.delete-btn');
  deleteBtns.forEach(btn => {
    btn.addEventListener('click', function(e) {
