score = 85

# 기본 if문 (콜론 필수!)
if score >= 90:
    print("A")
elif score >= 80:
    print("B")
elif score >= 70:
    print("C")
else:
    print("F")

# 삼항 연산자
result = "합격" if score >= 60 else "불합격"

# in 연산자
if "a" in "apple":
    print("포함")

# while문
x = 0
while x < 5:
    print(x, end=" ")  # 0 1 2 3 4
    x += 1

# break: 즉시 탈출
for i in range(10):
    if i == 5:
        break
    print(i, end=" ")  # 0 1 2 3 4

# continue: 건너뛰기
for i in range(5):
    if i == 2:
        continue
    print(i, end=" ")  # 0 1 3 4
