import React, { useState } from 'react';

function Counter() {
  // useState Hook으로 count 상태 생성
  const [count, setCount] = useState(0);

  // 증가 함수
  const increment = () => setCount(count + 1);

  // 감소 함수
  const decrement = () => setCount(count - 1);

  // 리셋 함수
  const reset = () => setCount(0);

  // 스타일 객체
  const styles = {
    container: {
      textAlign: 'center',
      padding: '50px',
      fontFamily: 'Arial, sans-serif'
    },
    count: {
      fontSize: '72px',
      fontWeight: 'bold',
      color: count > 0 ? '#28a745' : count < 0 ? '#dc3545' : '#333',
      margin: '20px 0'
    },
    button: {
      padding: '10px 25px',
      margin: '5px',
      fontSize: '18px',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      transition: 'transform 0.1s'
    },
    plusBtn: {
      background: '#28a745',
      color: 'white'
    },
    minusBtn: {
      background: '#dc3545',
      color: 'white'
    },
    resetBtn: {
      background: '#6c757d',
      color: 'white'
    }
  };

  return (
    <div style={styles.container}>
      <h1>React 카운터</h1>
      <div style={styles.count}>{count}</div>
      <div>
        <button
          style={{ ...styles.button, ...styles.plusBtn }}
          onClick={increment}
        >
          +1
        </button>
        <button
          style={{ ...styles.button, ...styles.minusBtn }}
          onClick={decrement}
        >
          -1
        </button>
        <button
          style={{ ...styles.button, ...styles.resetBtn }}
          onClick={reset}
        >
          초기화
        </button>
      </div>
    </div>
  );
}

export default Counter;

import React, { useState } from 'react';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');

  // 할일 추가
  const addTodo = () => {
    if (input.trim() === '') return;

    const newTodo = {
      id: Date.now(),
      text: input,
      done: false
    };

    setTodos([...todos, newTodo]);
    setInput('');
  };

  // 할일 삭제
  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  // 완료 토글
  const toggleDone = (id) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, done: !todo.done } : todo
    ));
  };

  // 엔터 키 처리
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') addTodo();
  };

  // 스타일
  const styles = {
    container: {
      maxWidth: '500px',
      margin: '50px auto',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    },
    inputGroup: {
      display: 'flex',
      marginBottom: '20px'
    },
    input: {
      flex: 1,
      padding: '12px',
      fontSize: '16px',
      border: '2px solid #ddd',
      borderRadius: '5px 0 0 5px',
      outline: 'none'
    },
    addBtn: {
      padding: '12px 20px',
      fontSize: '16px',
      background: '#28a745',
      color: 'white',
      border: 'none',
      borderRadius: '0 5px 5px 0',
      cursor: 'pointer'
    },
    todoItem: {
      display: 'flex',
      alignItems: 'center',
      padding: '12px',
      marginBottom: '10px',
      background: '#f8f9fa',
      borderRadius: '5px'
    },
    checkbox: {
      marginRight: '12px',
      cursor: 'pointer',
      width: '20px',
      height: '20px'
    },
    deleteBtn: {
      marginLeft: 'auto',
      padding: '5px 10px',
      background: '#dc3545',
      color: 'white',
      border: 'none',
      borderRadius: '3px',
      cursor: 'pointer'
    },
    stats: {
      textAlign: 'center',
      color: '#666',
      marginTop: '20px'
    }
  };

  const completedCount = todos.filter(t => t.done).length;

  return (
    <div style={styles.container}>
      <h1 style={{ textAlign: 'center' }}>할일 목록</h1>

      <div style={styles.inputGroup}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="할일을 입력하세요"
          style={styles.input}
        />
        <button onClick={addTodo} style={styles.addBtn}>
          추가
        </button>
      </div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {todos.map(todo => (
          <li key={todo.id} style={styles.todoItem}>
            <input
              type="checkbox"
              checked={todo.done}
              onChange={() => toggleDone(todo.id)}
              style={styles.checkbox}
            />
            <span style={{
              flex: 1,
              textDecoration: todo.done ? 'line-through' : 'none',
              color: todo.done ? '#999' : '#333'
            }}>
              {todo.text}
            </span>
            <button
              onClick={() => deleteTodo(todo.id)}
              style={styles.deleteBtn}
            >
              삭제
            </button>
          </li>
        ))}
      </ul>

      {todos.length > 0 && (
        <p style={styles.stats}>
          {completedCount} / {todos.length} 완료
        </p>
      )}
    </div>
  );
}

export default TodoList;

import React, { useState, useEffect, useRef } from 'react';

function Timer() {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [laps, setLaps] = useState([]);
  const intervalRef = useRef(null);

  // useEffect로 타이머 관리
  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setTime(prevTime => prevTime + 10);
      }, 10);
    } else {
      clearInterval(intervalRef.current);
    }

    // Cleanup 함수
    return () => clearInterval(intervalRef.current);
  }, [isRunning]);

  // 시간 포맷팅
  const formatTime = (ms) => {
    const minutes = Math.floor(ms / 60000);
    const seconds = Math.floor((ms % 60000) / 1000);
    const centiseconds = Math.floor((ms % 1000) / 10);

    return `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}.${String(centiseconds).padStart(2, '0')}`;
  };

  const start = () => setIsRunning(true);
  const stop = () => setIsRunning(false);
  const reset = () => {
    setIsRunning(false);
    setTime(0);
    setLaps([]);
  };
  const lap = () => {
    setLaps([...laps, time]);
  };

  const styles = {
    container: {
      textAlign: 'center',
      padding: '50px',
      fontFamily: 'monospace',
      background: '#1a1a2e',
      minHeight: '100vh',
      color: 'white'
    },
    time: {
      fontSize: '72px',
      fontWeight: 'bold',
      margin: '30px 0',
      color: isRunning ? '#4ecdc4' : '#ffffff'
    },
    button: {
      padding: '15px 30px',
      margin: '10px',
      fontSize: '18px',
      border: 'none',
      borderRadius: '50px',
      cursor: 'pointer',
      fontWeight: 'bold',
      transition: 'all 0.3s'
    },
    startBtn: {
      background: '#28a745',
      color: 'white'
    },
    stopBtn: {
      background: '#ffc107',
      color: '#333'
    },
    resetBtn: {
      background: '#dc3545',
      color: 'white'
    },
    lapBtn: {
      background: '#17a2b8',
      color: 'white'
    },
    lapList: {
      maxWidth: '300px',
      margin: '30px auto',
      textAlign: 'left'
    },
    lapItem: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '10px',
      borderBottom: '1px solid #333'
    }
  };

  return (
    <div style={styles.container}>
      <h1>스톱워치</h1>
      <div style={styles.time}>{formatTime(time)}</div>

      <div>
        {!isRunning ? (
          <button
            onClick={start}
            style={{ ...styles.button, ...styles.startBtn }}
          >
            시작
          </button>
        ) : (
          <button
            onClick={stop}
            style={{ ...styles.button, ...styles.stopBtn }}
          >
            정지
          </button>
        )}

        <button
          onClick={lap}
          disabled={!isRunning}
          style={{
            ...styles.button,
            ...styles.lapBtn,
            opacity: isRunning ? 1 : 0.5
          }}
        >
          랩
        </button>

        <button
          onClick={reset}
          style={{ ...styles.button, ...styles.resetBtn }}
        >
          리셋
        </button>
      </div>

      {laps.length > 0 && (
        <div style={styles.lapList}>
          <h3>랩 기록</h3>
          {laps.map((lapTime, index) => (
            <div key={index} style={styles.lapItem}>
              <span>랩 {index + 1}</span>
              <span>{formatTime(lapTime)}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Timer;

import React, { useState } from 'react';

function SearchFilter() {
  // 샘플 데이터
  const products = [
    { id: 1, name: '맥북 프로', category: '전자기기', price: 2500000 },
    { id: 2, name: '아이패드', category: '전자기기', price: 1200000 },
    { id: 3, name: '에어팟', category: '전자기기', price: 250000 },
    { id: 4, name: '나이키 운동화', category: '패션', price: 150000 },
    { id: 5, name: '리바이스 청바지', category: '패션', price: 120000 },
    { id: 6, name: '프로틴 파우더', category: '건강', price: 45000 },
    { id: 7, name: '요가 매트', category: '건강', price: 35000 },
    { id: 8, name: '캠핑 텐트', category: '아웃도어', price: 280000 },
  ];

  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('전체');
  const [sortBy, setSortBy] = useState('name');

  // 카테고리 목록 추출
  const categories = ['전체', ...new Set(products.map(p => p.category))];

  // 필터링 및 정렬
  const filteredProducts = products
    .filter(product => {
      const matchesSearch = product.name
        .toLowerCase()
        .includes(searchTerm.toLowerCase());
      const matchesCategory = 
        selectedCategory === '전체' || 
        product.category === selectedCategory;
      return matchesSearch && matchesCategory;
    })
    .sort((a, b) => {
      if (sortBy === 'name') return a.name.localeCompare(b.name);
      if (sortBy === 'price-asc') return a.price - b.price;
      if (sortBy === 'price-desc') return b.price - a.price;
      return 0;
    });

  const styles = {
    container: {
      maxWidth: '800px',
      margin: '50px auto',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    },
    searchBox: {
      width: '100%',
      padding: '15px',
      fontSize: '16px',
      border: '2px solid #ddd',
      borderRadius: '10px',
      marginBottom: '20px',
      outline: 'none'
    },
    filterBar: {
      display: 'flex',
      gap: '10px',
      flexWrap: 'wrap',
      marginBottom: '20px'
    },
    filterBtn: {
      padding: '10px 20px',
      border: 'none',
      borderRadius: '20px',
      cursor: 'pointer',
      transition: 'all 0.3s'
    },
    select: {
      padding: '10px 15px',
      borderRadius: '10px',
      border: '2px solid #ddd',
      fontSize: '14px'
    },
    productCard: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '15px 20px',
      marginBottom: '10px',
      background: '#f8f9fa',
      borderRadius: '10px',
      transition: 'transform 0.2s'
    },
    category: {
      padding: '5px 12px',
      background: '#e9ecef',
      borderRadius: '15px',
      fontSize: '12px'
    },
    price: {
      fontWeight: 'bold',
      color: '#28a745'
    },
    noResult: {
      textAlign: 'center',
      color: '#999',
      padding: '40px'
    }
  };

  const formatPrice = (price) => 
    price.toLocaleString('ko-KR') + '원';

  return (
    <div style={styles.container}>
      <h1 style={{ textAlign: 'center' }}>상품 검색</h1>

      <input
        type="text"
        placeholder="상품명으로 검색..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={styles.searchBox}
      />

      <div style={styles.filterBar}>
        {categories.map(cat => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            style={{
              ...styles.filterBtn,
              background: selectedCategory === cat ? '#007bff' : '#e9ecef',
              color: selectedCategory === cat ? 'white' : '#333'
            }}
          >
            {cat}
          </button>
        ))}

        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          style={styles.select}
        >
          <option value="name">이름순</option>
          <option value="price-asc">가격 낮은순</option>
          <option value="price-desc">가격 높은순</option>
        </select>
      </div>

      <p style={{ color: '#666', marginBottom: '10px' }}>
        {filteredProducts.length}개 상품
      </p>

      {filteredProducts.length > 0 ? (
        filteredProducts.map(product => (
          <div
            key={product.id}
            style={styles.productCard}
            onMouseOver={(e) => e.currentTarget.style.transform = 'translateX(5px)'}
            onMouseOut={(e) => e.currentTarget.style.transform = 'translateX(0)'}
          >
            <div>
              <strong>{product.name}</strong>
              <span style={{ ...styles.category, marginLeft: '10px' }}>
                {product.category}
              </span>
            </div>
            <span style={styles.price}>{formatPrice(product.price)}</span>
          </div>
        ))
      ) : (
        <div style={styles.noResult}>
          검색 결과가 없습니다.
        </div>
      )}
    </div>
  );
}

export default SearchFilter;

import React, { createContext, useContext, useState, useEffect } from 'react';

// Context 생성
const ThemeContext = createContext();

// Theme Provider 컴포넌트
export function ThemeProvider({ children }) {
  // localStorage에서 초기값 가져오기
  const [isDark, setIsDark] = useState(() => {
    const saved = localStorage.getItem('darkMode');
    return saved ? JSON.parse(saved) : false;
  });

  // 테마 변경 시 localStorage에 저장
  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(isDark));
    // body 클래스 토글 (전역 스타일용)
    document.body.classList.toggle('dark-mode', isDark);
  }, [isDark]);

  const toggleTheme = () => setIsDark(!isDark);

  // 테마 객체
  const theme = {
    isDark,
    toggleTheme,
    colors: isDark ? {
      background: '#1a1a2e',
      surface: '#16213e',
      text: '#ffffff',
      textSecondary: '#a0a0a0',
      primary: '#4ecdc4',
      border: '#333'
    } : {
      background: '#ffffff',
      surface: '#f8f9fa',
      text: '#333333',
      textSecondary: '#666666',
      primary: '#007bff',
      border: '#ddd'
    }
  };

  return (
    <ThemeContext.Provider value={theme}>
      {children}
    </ThemeContext.Provider>
  );
}

// Custom Hook
export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within ThemeProvider');
  }
  return context;
}

import React from 'react';
import { ThemeProvider, useTheme } from './ThemeContext';

// 다크모드 버튼 컴포넌트
function ThemeToggle() {
  const { isDark, toggleTheme } = useTheme();

  return (
    <button
      onClick={toggleTheme}
      style={{
        padding: '10px 20px',
        fontSize: '16px',
        background: isDark ? '#ffd700' : '#1a1a2e',
        color: isDark ? '#333' : '#fff',
        border: 'none',
        borderRadius: '25px',
        cursor: 'pointer',
        transition: 'all 0.3s'
      }}
    >
      {isDark ? ' 라이트 모드' : ' 다크 모드'}
    </button>
  );
}

// 카드 컴포넌트
function Card({ title, content }) {
  const { colors } = useTheme();

  return (
    <div style={{
      background: colors.surface,
      padding: '25px',
      borderRadius: '15px',
      marginBottom: '20px',
      border: `1px solid ${colors.border}`,
      transition: 'all 0.3s'
    }}>
      <h2 style={{ color: colors.text, marginBottom: '10px' }}>{title}</h2>
      <p style={{ color: colors.textSecondary }}>{content}</p>
    </div>
  );
}

// 메인 앱 컴포넌트
function Main() {
  const { colors } = useTheme();

  return (
    <div style={{
      minHeight: '100vh',
      background: colors.background,
      padding: '50px 20px',
      transition: 'all 0.3s'
    }}>
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '30px'
        }}>
          <h1 style={{ color: colors.text }}>다크 모드</h1>
          <ThemeToggle />
        </div>

        <Card 
          title="React Context API"
          content="Context를 사용하면 props drilling 없이 전역 상태를 관리할 수 있습니다."
        />
        <Card 
          title="다크 모드 구현"
          content="localStorage와 Context를 조합하여 테마 상태를 유지합니다."
        />
        <Card 
          title="CSS Transition"
          content="부드러운 전환 효과로 사용자 경험을 향상시킵니다."
        />
      </div>
    </div>
  );
}

// App 컴포넌트 (Provider 포함)
export default function App() {
  return (
    <ThemeProvider>
      <Main />
    </ThemeProvider>
  );
}

import React, { useState } from 'react';

function WeatherApp() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // OpenWeatherMap API 키 (https://openweathermap.org/api 에서 무료 발급)
  const API_KEY = 'YOUR_API_KEY';

  const fetchWeather = async () => {
    if (!city.trim()) {
      setError('도시명을 입력해주세요.');
      return;
    }

    setLoading(true);
    setError(null);
    setWeather(null);

    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric&lang=kr`
      );

      if (!response.ok) {
        throw new Error(
          response.status === 404 
            ? '도시를 찾을 수 없습니다.' 
            : '날씨 정보를 가져오는데 실패했습니다.'
        );
      }

      const data = await response.json();
      setWeather(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') fetchWeather();
  };

  // 날씨 아이콘 매핑
  const getWeatherIcon = (main) => {
    const icons = {
      'Clear': '',
      'Clouds': '',
      'Rain': '',
      'Drizzle': '',
      'Thunderstorm': '',
      'Snow': '',
      'Mist': '',
      'Fog': ''
    };
    return icons[main] || '';
  };

  const styles = {
    container: {
      maxWidth: '400px',
      margin: '50px auto',
      padding: '30px',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      borderRadius: '20px',
      color: 'white',
      fontFamily: 'Arial, sans-serif',
      boxShadow: '0 10px 40px rgba(0,0,0,0.3)'
    },
    input: {
      width: '100%',
      padding: '15px',
      fontSize: '16px',
      border: 'none',
      borderRadius: '10px',
      marginBottom: '15px',
      outline: 'none'
    },
    button: {
      width: '100%',
      padding: '15px',
      fontSize: '16px',
      background: 'rgba(255,255,255,0.2)',
      color: 'white',
      border: '2px solid white',
      borderRadius: '10px',
      cursor: 'pointer',
      fontWeight: 'bold',
      transition: 'all 0.3s'
    },
    weatherCard: {
      marginTop: '30px',
      background: 'rgba(255,255,255,0.1)',
      padding: '25px',
      borderRadius: '15px',
      textAlign: 'center'
    },
    temp: {
      fontSize: '64px',
      fontWeight: 'bold',
      margin: '10px 0'
    },
    error: {
      background: 'rgba(255,0,0,0.2)',
      padding: '15px',
      borderRadius: '10px',
      marginTop: '15px',
      textAlign: 'center'
    },
    loading: {
      textAlign: 'center',
      padding: '30px',
      fontSize: '24px'
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={{ textAlign: 'center', marginBottom: '25px' }}>
         날씨 정보
      </h1>

      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="도시 이름 입력 (예: Seoul, Tokyo)"
        style={styles.input}
      />

      <button
        onClick={fetchWeather}
        disabled={loading}
        style={{
          ...styles.button,
          opacity: loading ? 0.7 : 1
        }}
      >
        {loading ? '검색 중...' : ' 날씨 확인'}
      </button>

      {error && (
        <div style={styles.error}>
           {error}
        </div>
      )}

      {loading && (
        <div style={styles.loading}>

        </div>
      )}

      {weather && (
        <div style={styles.weatherCard}>
          <h2>{weather.name}, {weather.sys.country}</h2>
          <div style={{ fontSize: '80px', margin: '20px 0' }}>
            {getWeatherIcon(weather.weather[0].main)}
          </div>
          <div style={styles.temp}>
            {Math.round(weather.main.temp)}C
          </div>
          <p style={{ fontSize: '20px', marginBottom: '20px' }}>
            {weather.weather[0].description}
          </p>
          <div style={{ display: 'flex', justifyContent: 'space-around' }}>
            <div>
              <p> 습도</p>
              <p style={{ fontSize: '24px' }}>{weather.main.humidity}%</p>
            </div>
            <div>
              <p> 풍속</p>
              <p style={{ fontSize: '24px' }}>{weather.wind.speed} m/s</p>
            </div>
            <div>
              <p> 체감</p>
              <p style={{ fontSize: '24px' }}>
                {Math.round(weather.main.feels_like)}C
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default WeatherApp;
