@Configuration
public class RateLimitConfig {

    @Bean
    public Bucket4jRateLimiter rateLimiter() {
        // IPë‹¹ ë¶„ë‹¹ 100 ìš”ì²­, ë²„ìŠ¤íŠ¸ ìµœëŒ€ 20 í—ˆìš©
        return Bucket4jRateLimiter.builder()
            .addLimit(Bandwidth.classic(100, Refill.intervally(100, Duration.ofMinutes(1))))
            .addLimit(Bandwidth.classic(20, Refill.intervally(20, Duration.ofSeconds(1))))
            .build();
    }
}

@Component
public class RateLimitFilter extends OncePerRequestFilter {

    private final Map<String, Bucket> buckets = new ConcurrentHashMap<>();
    private static final Logger log = LoggerFactory.getLogger(RateLimitFilter.class);

    @Override
    protected void doFilterInternal(
            HttpServletRequest request,
            HttpServletResponse response,
            FilterChain filterChain) throws ServletException, IOException {

        String clientIp = getClientIp(request);
        Bucket bucket = buckets.computeIfAbsent(clientIp, this::createNewBucket);

        ConsumptionProbe probe = bucket.tryConsumeAndReturnRemaining(1);

        if (probe.isConsumed()) {
            // ë‚¨ì€ ìš”ì²­ ìˆ˜ í—¤ë”ì— í¬í•¨
            response.addHeader("X-Rate-Limit-Remaining", 
                String.valueOf(probe.getRemainingTokens()));
            filterChain.doFilter(request, response);
        } else {
            // 429 Too Many Requests
            log.warn("Rate limit exceeded for IP: {}", clientIp);
            response.setStatus(429);
            response.addHeader("Retry-After", 
                String.valueOf(probe.getNanosToWaitForRefill() / 1_000_000_000));
            response.getWriter().write("Too many requests. Please try again later.");
        }
    }

    private Bucket createNewBucket(String key) {
        return Bucket.builder()
            .addLimit(Bandwidth.classic(100, Refill.intervally(100, Duration.ofMinutes(1))))
            .addLimit(Bandwidth.classic(20, Refill.intervally(20, Duration.ofSeconds(1))))
            .build();
    }

    private String getClientIp(HttpServletRequest request) {
        String xForwardedFor = request.getHeader("X-Forwarded-For");
        if (xForwardedFor != null && !xForwardedFor.isEmpty()) {
            return xForwardedFor.split(",")[0].trim();
        }
        return request.getRemoteAddr();
    }
}

