    // íƒ€ì„ì•„ì›ƒì´ ìˆëŠ” ì •ê·œì‹ ë§¤ì¹­
    public boolean safeMatch(String input, Pattern pattern, long timeoutMs) {
        ExecutorService executor = Executors.newSingleThreadExecutor();
        Future<Boolean> future = executor.submit(() -> 
            pattern.matcher(input).matches()
        );

        try {
            return future.get(timeoutMs, TimeUnit.MILLISECONDS);
        } catch (TimeoutException e) {
            future.cancel(true);
            throw new ValidationException("Pattern matching timed out");
        } catch (Exception e) {
            throw new ValidationException("Pattern matching failed", e);
        } finally {
            executor.shutdownNow();
        }
    }

    // ì•ˆì „í•œ ì´ë©”ì¼ íŒ¨í„´ (ë°±íŠ¸ë˜í‚¹ ìµœì†Œí™”)
    private static final Pattern SAFE_EMAIL_PATTERN = 
