    @Autowired
    private EntityManager em;

    // âœ… Criteria API - íƒ€ì… ì•ˆì „í•œ ë™ì  ì¿¼ë¦¬
    public List<User> searchUsersSecure(String keyword, String sortBy, int page) {
        CriteriaBuilder cb = em.getCriteriaBuilder();
        CriteriaQuery<User> query = cb.createQuery(User.class);
        Root<User> user = query.from(User.class);

        // ê²€ìƒ‰ ì¡°ê±´ (íŒŒë¼ë¯¸í„° ë°”ì¸ë”© ìë™)
        List<Predicate> predicates = new ArrayList<>();
        if (keyword != null && !keyword.isBlank()) {
            predicates.add(cb.or(
                cb.like(cb.lower(user.get("name")), "%" + keyword.toLowerCase() + "%"),
                cb.like(cb.lower(user.get("email")), "%" + keyword.toLowerCase() + "%")
            ));
        }

        query.where(predicates.toArray(new Predicate[0]));

        // ì •ë ¬ (í™”ì´íŠ¸ë¦¬ìŠ¤íŠ¸ë¡œ ê²€ì¦ëœ ì»¬ëŸ¼ë§Œ)
        if (ALLOWED_SORT_COLUMNS.contains(sortBy)) {
            query.orderBy(cb.asc(user.get(sortBy)));
        }

        return em.createQuery(query)
                 .setFirstResult(page * 20)
                 .setMaxResults(20)
                 .getResultList();
    }

    private static final Set<String> ALLOWED_SORT_COLUMNS = 
        Set.of("name", "email", "createdAt");
}
