@Aspect
@Component
@Slf4j
public class SqlInjectionDetectorAspect {

    @Autowired
    private AlertService alertService;

    private static final Pattern SQL_INJECTION_PATTERN = Pattern.compile(
        "(?i)" +
        "(\\b(SELECT|INSERT|UPDATE|DELETE|DROP|UNION|CREATE|ALTER)\\b)" +
        "|(--|#|/\\*|\\*/|;)" +
        "|(\\b(OR|AND)\\b\\s+\\d+\\s*=\\s*\\d+)"
    );

    @Before("execution(* com.myapp.repository.*.*(..))")
    public void detectSqlInjection(JoinPoint joinPoint) {
        Object[] args = joinPoint.getArgs();

        for (Object arg : args) {
            if (arg instanceof String) {
                String value = (String) arg;
                if (SQL_INJECTION_PATTERN.matcher(value).find()) {
                    String method = joinPoint.getSignature().toShortString();

                    log.warn("[SQL INJECTION ATTEMPT] Method: {}, Value: {}", 
                             method, value);

                    // ë³´ì•ˆíŒ€ ì•Œë¦¼
                    alertService.sendSecurityAlert(
                        "SQL Injection ì‹œë„ íƒì§€",
                        String.format("Method: %s, Input: %s", method, value)
                    );

                    // ìš”ì²­ ì°¨ë‹¨
                    throw new SecurityException("Malicious input detected");
                }
            }
        }
    }
}
