@Service
public class HybridEncryptionService {

    private final RSAEncryptionService rsaService;
    private final AESEncryptionService aesService;

    // RSAë¡œ ëŒ€ì¹­í‚¤ ì•”í˜¸í™”, AESë¡œ ë°ì´í„° ì•”í˜¸í™”
    public EncryptedData encrypt(String data, PublicKey publicKey) throws Exception {
        // 1. ì„ì‹œ AES í‚¤ ìƒì„±
        KeyGenerator keyGen = KeyGenerator.getInstance("AES");
        keyGen.init(256);
        SecretKey aesKey = keyGen.generateKey();

        // 2. AESë¡œ ë°ì´í„° ì•”í˜¸í™” (ë¹ ë¦„)
        String encryptedData = aesService.encrypt(data, aesKey);

        // 3. RSAë¡œ AES í‚¤ ì•”í˜¸í™” (ì•ˆì „í•œ í‚¤ ì „ë‹¬)
        String encryptedKey = rsaService.encrypt(
            Base64.getEncoder().encodeToString(aesKey.getEncoded()), 
            publicKey
        );

        return new EncryptedData(encryptedData, encryptedKey);
    }
}

