@Service
@RequiredArgsConstructor
public class UserService {

    private final UserRepository userRepository;
    private final PasswordEncoder passwordEncoder;

    @Transactional
    public User register(RegisterRequest request) {
        // 1. ì´ë©”ì¼ ì¤‘ë³µ ì²´í¬
        if (userRepository.existsByEmail(request.getEmail())) {
            throw new DuplicateEmailException("ì´ë¯¸ ì‚¬ìš© ì¤‘ì¸ ì´ë©”ì¼ì…ë‹ˆë‹¤");
        }

        // 2. ë¹„ë°€ë²ˆí˜¸ ê°•ë„ ê²€ì¦
        validatePasswordStrength(request.getPassword());

        // 3. ë¹„ë°€ë²ˆí˜¸ í•´ì‹œ í›„ ì €ì¥
        User user = User.builder()
                .email(request.getEmail())
                .password(passwordEncoder.encode(request.getPassword())) // í•´ì‹œ!
                .name(request.getName())
                .build();

        return userRepository.save(user);
    }

    public User login(LoginRequest request) {
        User user = userRepository.findByEmail(request.getEmail())
                .orElseThrow(() -> new AuthException("ì´ë©”ì¼ ë˜ëŠ” ë¹„ë°€ë²ˆí˜¸ê°€ ì˜ëª»ë˜ì—ˆìŠµë‹ˆë‹¤"));

        // í•´ì‹œëœ ë¹„ë°€ë²ˆí˜¸ì™€ ë¹„êµ
        if (!passwordEncoder.matches(request.getPassword(), user.getPassword())) {
            throw new AuthException("ì´ë©”ì¼ ë˜ëŠ” ë¹„ë°€ë²ˆí˜¸ê°€ ì˜ëª»ë˜ì—ˆìŠµë‹ˆë‹¤");
        }

        return user;
    }

    private void validatePasswordStrength(String password) {
        if (password.length() < 8) {
            throw new WeakPasswordException("ë¹„ë°€ë²ˆí˜¸ëŠ” 8ì ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤");
        }
        if (!password.matches(".*[A-Z].*")) {
            throw new WeakPasswordException("ëŒ€ë¬¸ìë¥¼ í¬í•¨í•´ì•¼ í•©ë‹ˆë‹¤");
        }
        if (!password.matches(".*[0-9].*")) {
            throw new WeakPasswordException("ìˆ«ìë¥¼ í¬í•¨í•´ì•¼ í•©ë‹ˆë‹¤");
        }
        if (!password.matches(".*[!@#$%^&*].*")) {
            throw new WeakPasswordException("íŠ¹ìˆ˜ë¬¸ìë¥¼ í¬í•¨í•´ì•¼ í•©ë‹ˆë‹¤");
        }
    }
}

