import javax.net.ssl.*;
import java.security.cert.X509Certificate;

// âœ… ì˜¬ë°”ë¥¸ HTTPS í´ë¼ì´ì–¸íŠ¸ ì„¤ì •
public class SecureHttpClient {

    public HttpClient createSecureClient() {
        return HttpClient.newBuilder()
                .version(HttpClient.Version.HTTP_2)  // HTTP/2 over TLS
                .connectTimeout(Duration.ofSeconds(10))
                .sslContext(SSLContext.getDefault()) // ì‹œìŠ¤í…œ ê¸°ë³¸ CA ì‚¬ìš©
                .build();
    }

    public String fetchSecure(String url) throws Exception {
        HttpClient client = createSecureClient();
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .GET()
                .build();

        HttpResponse<String> response = client.send(
                request, HttpResponse.BodyHandlers.ofString());

        return response.body();
    }
}

