    // MFA ìš”ì²­ ì œí•œ (í”¼ë¡œ ê³µê²© ë°©ì§€)
    public void checkMfaRateLimit(Long userId) {
        String key = "mfa:request:" + userId;
        String countStr = redisTemplate.opsForValue().get(key);
        int count = countStr != null ? Integer.parseInt(countStr) : 0;

        if (count >= 3) {  // 5ë¶„ ë‚´ 3íšŒ ì´ˆê³¼
            throw new MfaException("ë„ˆë¬´ ë§ì€ ì¸ì¦ ìš”ì²­ì…ë‹ˆë‹¤. ì ì‹œ í›„ ë‹¤ì‹œ ì‹œë„í•˜ì„¸ìš”.");
        }

        redisTemplate.opsForValue().increment(key);
        redisTemplate.expire(key, 5, TimeUnit.MINUTES);
    }

    // ìˆ«ì ë§¤ì¹­ ë°©ì‹ (í‘¸ì‹œ ì•Œë¦¼ìš©)
    public NumberMatchChallenge generateNumberMatch() {
        SecureRandom random = new SecureRandom();
        int correctNumber = random.nextInt(100);  // 0-99

        List<Integer> options = new ArrayList<>();
        options.add(correctNumber);

        while (options.size() < 3) {
            int option = random.nextInt(100);
            if (!options.contains(option)) {
                options.add(option);
            }
        }
        Collections.shuffle(options);

        return NumberMatchChallenge.builder()
            .correctNumber(correctNumber)
            .options(options)
            .build();
    }
}
