            return;
        }

        // ì„œëª… ê²€ì¦
        String dataToVerify = timestamp + "|" + body;
        if (!signatureService.verify(dataToVerify, signature, partnerPublicKey)) {
            response.sendError(401, "ì„œëª… ê²€ì¦ ì‹¤íŒ¨");
            return;
        }

        chain.doFilter(request, response);
    }
}

