// ë§ˆìŠ¤í‚¹ ì»¨ë²„í„° êµ¬í˜„
public class MaskingConverter extends MessageConverter {
    private static final Pattern CARD_PATTERN = 
        Pattern.compile("(\\d{4})-?(\\d{4})-?(\\d{4})-?(\\d{4})");
    private static final Pattern SSN_PATTERN = 
        Pattern.compile("(\\d{6})-?(\\d{7})");
    private static final Pattern PASSWORD_PATTERN = 
        Pattern.compile("(password|pwd|passwd)[=:]\\s*([^\\s,}]+)", Pattern.CASE_INSENSITIVE);

    @Override
    public String convert(ILoggingEvent event) {
        String message = event.getFormattedMessage();

        // ì¹´ë“œë²ˆí˜¸ ë§ˆìŠ¤í‚¹: 1234-5678-9012-3456 -> 1234-****-****-3456
        message = CARD_PATTERN.matcher(message)
            .replaceAll("$1-****-****-$4");

        // ì£¼ë¯¼ë²ˆí˜¸ ë§ˆìŠ¤í‚¹: 900101-1234567 -> 900101-*******
        message = SSN_PATTERN.matcher(message)
            .replaceAll("$1-*******");

        // ë¹„ë°€ë²ˆí˜¸ ë§ˆìŠ¤í‚¹: password=secret -> password=*****
        message = PASSWORD_PATTERN.matcher(message)
            .replaceAll("$1=*****");

        return message;
    }
}

