def count_a(s):
    count = 0
    for char in s:
        if char == 'a':
            count += 1
    return count

def get_evens(numbers):
    result = []
    for n in numbers:
        if n % 2 == 0:
            result.append(n)
    return result

def get_grade(score):
    if score >= 90:
        return 'A'
    elif score >= 80:
        return 'B'
    elif score >= 70:
        return 'C'
    else:
        return 'F'

def sum_to_n(n):
    total = 0
    for i in range(1, n + 1):
        total += i
    return total

def sum_2d(matrix):
    total = 0
    for row in matrix:
        for num in row:
            total += num
    return total

